/**
 * Get editorial content for a specific city
 * @param citySlug - City slug (e.g., 'austin', 'los-angeles')
 * @returns CityEditorialContent object or null if not found
 */
export function getCityEditorialContent(
  citySlug: string
): CityEditorialContent | null {
  return (
    CITY_EDITORIAL_CONTENT.find((content) => content.citySlug === citySlug) ||
    null
  )
}

/**
 * Get all city editorial content
 * @returns Array of all CityEditorialContent objects
 */
export function getAllCityEditorialContent(): CityEditorialContent[] {
  return CITY_EDITORIAL_CONTENT
}
